mapper.update:
update novel_user
set user_name = #{userName,jdbcType=VARCHAR},
  user_acount = #{userAcount,jdbcType=VARCHAR},
  facebook_id = #{facebookId,jdbcType=VARCHAR},
  user_status = #{userStatus,jdbcType=VARCHAR}
where user_id = #{userId,jdbcType=INTEGER}

mapper.select:
select user_id, user_name, user_acount, facebook_id, user_status
from novel_user
where user_id = #{userId,jdbcType=INTEGER}
